// Proses NIK
$noaskes = str_replace(array("-", " "), "", $params["value"]);

//proses bridging pcare mencari data peserta by nik
// Setup konfigurasi kredensial (sesuaikan dengan variabel Anda)
loadSecrets();
$keyenc=RM;
$cons=msy_decrypt('cons',$keyenc);
$password=msy_decrypt('password',$keyenc);
$user_key=msy_decrypt('user_key',$keyenc);
$secret=msy_decrypt('secret',$keyenc);



$sp=DB::PrepareSQL("select user1,kode_provider,".$cons.",".$password.",".$user_key.",".$secret." from puskesmas where pusk_id=:1",$_SESSION["puskesmas"]);
//echo $sp;
//exit();
$rs=DB::Query($sp);
while( $data = $rs->fetchAssoc() )
{
$cons1 = $data["cons"];
$password1 = $data["password"];
$user_key1 = $data["user_key"];
$user1 = $data["user1"];
$secret1= $data["secret"];
}

	date_default_timezone_set('UTC'); 
	$stamp = strval(time()-strtotime('1970-01-01 00:00:00'));
	//$stamp		= time();
	$data 		= $cons1.'&'.$stamp;
	$key		= $cons1.$secret1.$stamp;

$config = array(
    'consID'     => $cons1,
    'secretKey'  => $secret1,
    'user_key'   => $user_key1,
    'pcareUname' => $user1,
    'pcarePWD'   => $password1,
    'kdAplikasi' => '095' // default, bisa diubah jika perlu
);

// Base URL PCare
$base_url = $_SESSION["base_url_pcare"];

// ============ CONTOH 1: Get Peserta by NOKA ============

$result_pcare = pcare_get_peserta_noka($base_url, $noaskes, $config);

if ($result_pcare['success']) {
    $responseData = $result_pcare['data']["response"];
    $decryptedData = decrypt_pcare_data($responseData, $key);
    //$decryptedData=json_decode($decryptedData, true);
    
    if ($decryptedData !== null) {
        // echo "Decrypt berhasil!<br>";
        // echo "Data count: " . count($decryptedData) . "<br>";
        // echo "<pre>";
        // print_r($decryptedData);
        // echo "</pre>";
        $result["noKTP"] = $decryptedData["noKTP"];
        $result["nama"] = $decryptedData["nama"];
        $result["sex"] = $decryptedData["sex"];
        $result["tglLahir"] = $decryptedData["tglLahir"];
        $result["tglMulaiAktif"] = $decryptedData["tglMulaiAktif"];
        $result["tglAkhirBerlaku"] = $decryptedData["tglAkhirBerlaku"];
        $result["nmProvider"] = $decryptedData["kdProviderPst"]["nmProvider"];
        $result["jnsKelas"] = $decryptedData["jnsKelas"]["nama"];
        $result["jnsPeserta"] = $decryptedData["jnsPeserta"]["nama"];
        $result["golDarah"] = $decryptedData["golDarah"];
        $result["noHP"] = $decryptedData["noHP"];
        $result["ketAktif"] = $decryptedData["ketAktif"];
        $result["tunggakan"] = $decryptedData["tunggakan"];
        
    } else {
        $errorMsg = get_pcare_decrypt_error();
        echo "Error Decrypt: " . $errorMsg;
    }
}
