$(document).ready(function() {
    
    function calculateAge() {
        var tglLahirCtrl = Runner.getControl(pageid, 'tgl_lahir');
        var tglCtrl = Runner.getControl(pageid, 'tgl');
        
        if (!tglLahirCtrl || !tglCtrl) return;
        
        var tglLahir = tglLahirCtrl.getValue();
        var tgl = tglCtrl.getValue();
        
        if (!tglLahir || !tgl) return;
        
        // Handle object Date or string
        var birth = (tglLahir instanceof Date) ? tglLahir : parseDate(tglLahir);
        var ref = (tgl instanceof Date) ? tgl : parseDate(tgl);
        
        if (!birth || !ref || birth > ref) {
            if (birth > ref) {
                alert('Error: Tanggal lahir tidak boleh lebih besar dari tanggal periksa!');
            }
            Runner.getControl(pageid, 'umur_t').setValue('');
            Runner.getControl(pageid, 'umur_b').setValue('');
            Runner.getControl(pageid, 'umur_h').setValue('');
            Runner.getControl(pageid, 'usia').setValue('');
            return;
        }
        
        // Calculate age
        var years = ref.getFullYear() - birth.getFullYear();
        var months = ref.getMonth() - birth.getMonth();
        var days = ref.getDate() - birth.getDate();
        
        // Adjust negative days
        if (days < 0) {
            var lastMonth = new Date(ref.getFullYear(), ref.getMonth(), 0);
            days += lastMonth.getDate();
            months--;
        }
        
        // Adjust negative months
        if (months < 0) {
            months += 12;
            years--;
        }
        
        // Set values
        Runner.getControl(pageid, 'umur_t').setValue(years);
        Runner.getControl(pageid, 'umur_b').setValue(months);
        Runner.getControl(pageid, 'umur_h').setValue(days);
        
        var usia = '';
        if (years > 0) usia += years + ' tahun ';
        if (months > 0) usia += months + ' bulan ';
        if (days > 0) usia += days + ' hari';
        
        Runner.getControl(pageid, 'usia').setValue(usia.trim());
    }
    
    function parseDate(dateStr) {
        if (!dateStr || typeof dateStr !== 'string') return null;
        
        var parts = dateStr.split('/');
        if (parts.length === 3) {
            var day = parseInt(parts[0]);
            var month = parseInt(parts[1]) - 1;
            var year = parseInt(parts[2]);
            
            var date = new Date(year, month, day);
            
            if (date.getFullYear() === year && 
                date.getMonth() === month && 
                date.getDate() === day) {
                return date;
            }
        }
        
        var isoDate = new Date(dateStr);
        if (!isNaN(isoDate.getTime())) {
            return isoDate;
        }
        
        return null;
    }
    
    // Bind events
    Runner.getControl(pageid, 'tgl_lahir').on('change', calculateAge);
    Runner.getControl(pageid, 'tgl').on('change', calculateAge);
    
    // Initial trigger
    setTimeout(calculateAge, 500);
});
