function ss_timestamp($timezone = null) {
    // Gunakan timezone dari argumen, atau fallback ke WIB jika kosong/tidak valid
    $tz = trim((string)$timezone);

    // Daftar timezone yang diizinkan (bisa diperluas jika perlu)
    $allowed = ['Asia/Jakarta', 'Asia/Makassar', 'Asia/Jayapura', 'UTC'];

    // Validasi minimal: jika tidak di daftar izin, pakai default
    if (!in_array($tz, $allowed)) {
        $tz = 'Asia/Jakarta';
    }

    try {
        $dt = new DateTime('now', new DateTimeZone($tz));
        return $dt->format('Y-m-d\TH:i:s.vP'); // Format SatuSehat resmi
    } catch (Exception $e) {
        // Fallback aman jika DateTime gagal
        $dt = new DateTime('now', new DateTimeZone('Asia/Jakarta'));
        return $dt->format('Y-m-d\TH:i:s.vP');
    }
}


function genid() {
    $micro = sprintf('%.6f', microtime(true));
    $time = str_replace('.', '', $micro);
    $rand = bin2hex(random_bytes(6));
    return $time . $rand;
}


function phprunnerApiRequest(
    $url,
    $method = "GET",              // default GET
    $urlParams = [],              // default kosong
    $headers = ["Content-Type" => "application/json"], // default header JSON
    $postPayload = [],            // default kosong
    $body = null                  // default null
) {
    // Buat objek HttpRequest
    $request = new HttpRequest($url);

    // Tentukan method
    $request->method = strtoupper($method);

    // Tambahkan parameter URL jika ada
    foreach ($urlParams as $key => $value) {
        $request->urlParams[$key] = $value;
    }

    // Tambahkan headers (gabungkan default + custom)
    foreach ($headers as $key => $value) {
        $request->headers[$key] = $value;
    }

    // Jika body tidak null, gunakan body
    if ($body !== null) {
        $request->body = $body;
    } else {
        // Jika body null, gunakan postPayload
        foreach ($postPayload as $key => $value) {
            $request->postPayload[$key] = $value;
        }
    }

    // Jalankan request
    $response = $request->run();

    return $response;
}


